import React, { useState, useReselect } from 'react';
import { selectedItemsSlice } from '../features/selectedItemsSlice';

const OutfitView = () => {
  const [selectedItems, setSelectedItems] = useState([]);

  // Get the selected items from the store using the custom selector
  const selectedItemsFromStore = useReselect(selectedItemsSlice);

  // Update the selected items state
  const handleAddItem = (item) => {
    setSelectedItems([...selectedItems, item]);
  };

  const handleRemoveItem = (item) => {
    setSelectedItems(selectedItems.filter(selectedItem => selectedItem !== item));
  };

  return (
    <div>
      <h1>Outfit View</h1>
      <ul>
        {selectedItemsFromStore.map(item => (
          <li key={item.id}>
            {item.name}
            <button onClick={() => handleRemoveItem(item)}>Remove</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default OutfitView;
